import { useEffect, useState } from 'react';
import './App.css';
import Electionabi from './contracts/Election.json';
import Web3 from "web3";
import Navbar from './Components/Navbar';

function App() {

  const [currentAccount, setCurrentAccount] = useState('');
  const [loader, setLoader] = useState(true);
  const [electionData, setElectionData] = useState();

  useEffect(() => {
    loadWeb3();
    loadBlockchaindata();
  }, [])

  const loadWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    }
    else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying metamask!"
      )
    }
  }

  const loadBlockchaindata = async () => {
    setLoader(true);
    const web3 = window.web3;

    const accounts = await web3.eth.getAccounts();
    const account = accounts[0];
    setCurrentAccount(account);

    const networkId = await web3.eth.net.getId();

    const networkData = Electionabi.networks[networkId];

    if (networkData) {
      const election = new web3.eth.Contract(Electionabi.abi, networkData.address);
      const candidate1 = await election.methods.candidates(1).call();
      const candidate2 = await election.methods.candidates(2).call();
      console.log(candidate1)
      console.log(candidate2)
      setElectionData(election);
      setLoader(false);
      console.log(election);
    }
    else {
      window.alert("The smart contract is not deployed current network")
    }
  }

  if (loader) {
    return (
      <div className="container">
        <div className="d-flex align-items-center justify-content-center" style={{minHeight:'100vh'}}>
          <div className="spinner-grow text-primary" role="status">
            <span className="sr-only"></span>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="App">
      <Navbar currentAccount={currentAccount} />
    </div>
  );
}

export default App;
