import React, { useState } from "react";

export default function Content({ candidate1, candidate2, currentAccount, vote }) {
    const [selectedCandidate, setSelectedCandidate] = useState();
    const handleSubmit = (e) => {
        if ([candidate1.id, candidate2.id].includes(selectedCandidate)){
        // vote(Number(selectedCandidate));
        console.log('done')
        }
        else{
            alert('Error in submission');
        }
    }

    return (
        <div className="container mt-5">
            <div className="container">
                <p className="fs-5">Voting id : <b>{currentAccount}</b></p>
            </div>
            <div className="container">
                <h3 className="fs-1">Caste your Vote</h3>
                <div className="mt-3">
                    <div>
                        <div className="form-check fs-5">
                            <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" />
                            <label className="form-check-label" htmlFor="flexRadioDefault1">
                                {candidate1.name}
                            </label>
                        </div>
                        <div className="form-check fs-5">
                            <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" />
                            <label className="form-check-label" htmlFor="flexRadioDefault2">
                                {candidate2.name}
                            </label>
                        </div>
                    </div>
                    <button className="btn btn-success mt-3 col-md-1" type="submit" onSubmit={handleSubmit}>Vote</button>
                </div>
            </div>
        </div>
    );
}
